public class ShiftOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		System.out.println("Signed Right shift operator:"+(x>>2));
		System.out.println("Signed Left shift operator:"+(x<<2));
		
	}

}
